00044 /* Lists for ready and blocked co-routines. --------------------*/
00045 static List_t pxReadyCoRoutineLists[ configMAX_CO_ROUTINE_PRIORITIES ]; /*< Prioritised ready co-routines. */
00046 static List_t xDelayedCoRoutineList1;                                   /*< Delayed co-routines. */
00047 static List_t xDelayedCoRoutineList2;                                   /*< Delayed co-routines (two lists are used - one for delays that have overflowed the current tick count. */
00048 static List_t * pxDelayedCoRoutineList;                                 /*< Points to the delayed co-routine list currently being used. */
00049 static List_t * pxOverflowDelayedCoRoutineList;                         /*< Points to the delayed co-routine list currently being used to hold co-routines that have overflowed the current tick count. */
00050 static List_t xPendingReadyCoRoutineList;                               /*< Holds co-routines that have been readied by an external event.  They cannot be added directly to the ready lists as the ready lists cannot be accessed by interrupts. */
00051 
00052 /* Other file private variables. --------------------------------*/
00053 CRCB_t * pxCurrentCoRoutine = NULL;
00054 static UBaseType_t uxTopCoRoutineReadyPriority = 0;
00055 static TickType_t xCoRoutineTickCount = 0, xLastTickCount = 0, xPassedTicks = 0;
00056 
00057 /* The initial state of the co-routine when it is created. */
00058 #define corINITIAL_STATE    ( 0 )
00059 
00060 /*
00061  * Place the co-routine represented by pxCRCB into the appropriate ready queue
00062  * for the priority.  It is inserted at the end of the list.
00063  *
00064  * This macro accesses the co-routine ready lists and therefore must not be
00065  * used from within an ISR.
00066  */
00067 #define prvAddCoRoutineToReadyQueue( pxCRCB )                                                                       \
00068 {                                                                                                                   \
00069     if( pxCRCB->uxPriority > uxTopCoRoutineReadyPriority )                                                          \
00070     {                                                                                                               \
00071         uxTopCoRoutineReadyPriority = pxCRCB->uxPriority;                                                           \
00072     }                                                                                                               \
00073     vListInsertEnd( ( List_t * ) &( pxReadyCoRoutineLists[ pxCRCB->uxPriority ] ), &( pxCRCB->xGenericListItem ) ); \
00074 }
00075 
00076 /*
00077  * Utility to ready all the lists used by the scheduler.  This is called
00078  * automatically upon the creation of the first co-routine.
00079  */
00080 static void prvInitialiseCoRoutineLists( void );
00081 
00082 /*
00083  * Co-routines that are readied by an interrupt cannot be placed directly into
00084  * the ready lists (there is no mutual exclusion).  Instead they are placed in
00085  * in the pending ready list in order that they can later be moved to the ready
00086  * list by the co-routine scheduler.
00087  */
00088 static void prvCheckPendingReadyList( void );
00089 
00090 /*
00091  * Macro that looks at the list of co-routines that are currently delayed to
00092  * see if any require waking.
00093  *
00094  * Co-routines are stored in the queue in the order of their wake time -
00095  * meaning once one co-routine has been found whose timer has not expired
00096  * we need not look any further down the list.
00097  */
00098 static void prvCheckDelayedList( void );
00099 
