00102 BaseType_t xCoRoutineCreate( crCOROUTINE_CODE pxCoRoutineCode, UBaseType_t uxPriority, UBaseType_t uxIndex )
00103 {
00104 BaseType_t xReturn;
00105 CRCB_t *pxCoRoutine;
00106 
00107     /* Allocate the memory that will store the co-routine control block. */
00108     pxCoRoutine = ( CRCB_t * ) pvPortMalloc( sizeof( CRCB_t ) );
00109     if( pxCoRoutine )
00110     {
00111         /* If pxCurrentCoRoutine is NULL then this is the first co-routine to
00112         be created and the co-routine data structures need initialising. */
00113         if( pxCurrentCoRoutine == NULL )
00114         {
00115             pxCurrentCoRoutine = pxCoRoutine;
00116             prvInitialiseCoRoutineLists();
00117         }
00118 
00119         /* Check the priority is within limits. */
00120         if( uxPriority >= configMAX_CO_ROUTINE_PRIORITIES )
00121         {
00122             uxPriority = configMAX_CO_ROUTINE_PRIORITIES - 1;
00123         }
00124 
00125         /* Fill out the co-routine control block from the function parameters. */
00126         pxCoRoutine->uxState = corINITIAL_STATE;
00127         pxCoRoutine->uxPriority = uxPriority;
00128         pxCoRoutine->uxIndex = uxIndex;
00129         pxCoRoutine->pxCoRoutineFunction = pxCoRoutineCode;
00130 
00131         /* Initialise all the other co-routine control block parameters. */
00132         vListInitialiseItem( &( pxCoRoutine->xGenericListItem ) );
00133         vListInitialiseItem( &( pxCoRoutine->xEventListItem ) );
00134 
00135         /* Set the co-routine control block as a link back from the ListItem_t.
00136         This is so we can get back to the containing CRCB from a generic item
00137         in a list. */
00138         listSET_LIST_ITEM_OWNER( &( pxCoRoutine->xGenericListItem ), pxCoRoutine );
00139         listSET_LIST_ITEM_OWNER( &( pxCoRoutine->xEventListItem ), pxCoRoutine );
00140 
00141         /* Event lists are always in priority order. */
00142         listSET_LIST_ITEM_VALUE( &( pxCoRoutine->xEventListItem ), ( ( TickType_t ) configMAX_CO_ROUTINE_PRIORITIES - ( TickType_t ) uxPriority ) );
00143 
00144         /* Now the co-routine has been initialised it can be added to the ready
00145         list at the correct priority. */
00146         prvAddCoRoutineToReadyQueue( pxCoRoutine );
00147 
00148         xReturn = pdPASS;
00149     }
00150     else
00151     {
00152         xReturn = errCOULD_NOT_ALLOCATE_REQUIRED_MEMORY;
00153     }
00154 
00155     return xReturn;
00156 }
