00158 
00159 void vCoRoutineAddToDelayedList( TickType_t xTicksToDelay, List_t *pxEventList )
00160 {
00161 TickType_t xTimeToWake;
00162 
00163     /* Calculate the time to wake - this may overflow but this is
00164     not a problem. */
00165     xTimeToWake = xCoRoutineTickCount + xTicksToDelay;
00166 
00167     /* We must remove ourselves from the ready list before adding
00168     ourselves to the blocked list as the same list item is used for
00169     both lists. */
00170     ( void ) uxListRemove( ( ListItem_t * ) &( pxCurrentCoRoutine->xGenericListItem ) );
00171 
00172     /* The list item will be inserted in wake time order. */
00173     listSET_LIST_ITEM_VALUE( &( pxCurrentCoRoutine->xGenericListItem ), xTimeToWake );
00174 
00175     if( xTimeToWake < xCoRoutineTickCount )
00176     {
00177         /* Wake time has overflowed.  Place this item in the
00178         overflow list. */
00179         vListInsert( ( List_t * ) pxOverflowDelayedCoRoutineList, ( ListItem_t * ) &( pxCurrentCoRoutine->xGenericListItem ) );
00180     }
00181     else
00182     {
00183         /* The wake time has not overflowed, so we can use the
00184         current block list. */
00185         vListInsert( ( List_t * ) pxDelayedCoRoutineList, ( ListItem_t * ) &( pxCurrentCoRoutine->xGenericListItem ) );
00186     }
00187 
00188     if( pxEventList )
00189     {
00190         /* Also add the co-routine to an event list.  If this is done then the
00191         function must be called with interrupts disabled. */
00192         vListInsert( pxEventList, &( pxCurrentCoRoutine->xEventListItem ) );
00193     }
00194 }
00195 /*-----------------------------------------------------------*/
00196 
00197 static void prvCheckPendingReadyList( void )
00198 {
00199     /* Are there any co-routines waiting to get moved to the ready list?  These
00200     are co-routines that have been readied by an ISR.  The ISR cannot access
00201     the ready lists itself. */
00202     while( listLIST_IS_EMPTY( &xPendingReadyCoRoutineList ) == pdFALSE )
00203     {
00204         CRCB_t *pxUnblockedCRCB;
00205 
00206         /* The pending ready list can be accessed by an ISR. */
00207         portDISABLE_INTERRUPTS();
00208         {
00209             pxUnblockedCRCB = ( CRCB_t * ) listGET_OWNER_OF_HEAD_ENTRY( (&xPendingReadyCoRoutineList) );
00210             ( void ) uxListRemove( &( pxUnblockedCRCB->xEventListItem ) );
00211         }
00212         portENABLE_INTERRUPTS();
00213 
00214         ( void ) uxListRemove( &( pxUnblockedCRCB->xGenericListItem ) );
00215         prvAddCoRoutineToReadyQueue( pxUnblockedCRCB );
00216     }
00217 }
00218 /*-----------------------------------------------------------*/
00219 
00220 static void prvCheckDelayedList( void )
00221 {
00222 CRCB_t *pxCRCB;
00223 
00224     xPassedTicks = xTaskGetTickCount() - xLastTickCount;
00225     while( xPassedTicks )
00226     {
00227         xCoRoutineTickCount++;
00228         xPassedTicks--;
00229 
00230         /* If the tick count has overflowed we need to swap the ready lists. */
00231         if( xCoRoutineTickCount == 0 )
00232         {
00233             List_t * pxTemp;
00234 
00235             /* Tick count has overflowed so we need to swap the delay lists.  If there are
00236             any items in pxDelayedCoRoutineList here then there is an error! */
00237             pxTemp = pxDelayedCoRoutineList;
00238             pxDelayedCoRoutineList = pxOverflowDelayedCoRoutineList;
00239             pxOverflowDelayedCoRoutineList = pxTemp;
00240         }
00241 
00242         /* See if this tick has made a timeout expire. */
00243         while( listLIST_IS_EMPTY( pxDelayedCoRoutineList ) == pdFALSE )
00244         {
00245             pxCRCB = ( CRCB_t * ) listGET_OWNER_OF_HEAD_ENTRY( pxDelayedCoRoutineList );
00246 
00247             if( xCoRoutineTickCount < listGET_LIST_ITEM_VALUE( &( pxCRCB->xGenericListItem ) ) )
00248             {
00249                 /* Timeout not yet expired. */
00250                 break;
00251             }
00252 
00253             portDISABLE_INTERRUPTS();
00254             {
00255                 /* The event could have occurred just before this critical
00256                 section.  If this is the case then the generic list item will
00257                 have been moved to the pending ready list and the following
00258                 line is still valid.  Also the pvContainer parameter will have
00259                 been set to NULL so the following lines are also valid. */
00260                 ( void ) uxListRemove( &( pxCRCB->xGenericListItem ) );
00261 
00262                 /* Is the co-routine waiting on an event also? */
00263                 if( pxCRCB->xEventListItem.pxContainer )
00264                 {
00265                     ( void ) uxListRemove( &( pxCRCB->xEventListItem ) );
00266                 }
00267             }
00268             portENABLE_INTERRUPTS();
00269 
00270             prvAddCoRoutineToReadyQueue( pxCRCB );
00271         }
00272     }
00273 
00274     xLastTickCount = xCoRoutineTickCount;
00275 }
00276 /*-----------------------------------------------------------*/
00277 
00278 void vCoRoutineSchedule( void )
00279 {
00280     /* See if any co-routines readied by events need moving to the ready lists. */
00281     prvCheckPendingReadyList();
00282 
00283     /* See if any delayed co-routines have timed out. */
00284     prvCheckDelayedList();
00285 
00286     /* Find the highest priority queue that contains ready co-routines. */
00287     while( listLIST_IS_EMPTY( &( pxReadyCoRoutineLists[ uxTopCoRoutineReadyPriority ] ) ) )
00288     {
00289         if( uxTopCoRoutineReadyPriority == 0 )
00290         {
00291             /* No more co-routines to check. */
00292             return;
00293         }
00294         --uxTopCoRoutineReadyPriority;
00295     }
00296 
00297     /* listGET_OWNER_OF_NEXT_ENTRY walks through the list, so the co-routines
00298      of the same priority get an equal share of the processor time. */
00299     listGET_OWNER_OF_NEXT_ENTRY( pxCurrentCoRoutine, &( pxReadyCoRoutineLists[ uxTopCoRoutineReadyPriority ] ) );
00300 
00301     /* Call the co-routine. */
00302     ( pxCurrentCoRoutine->pxCoRoutineFunction )( pxCurrentCoRoutine, pxCurrentCoRoutine->uxIndex );
00303 
00304     return;
00305 }
00306 /*-----------------------------------------------------------*/
00307 
00308 static void prvInitialiseCoRoutineLists( void )
00309 {
00310 UBaseType_t uxPriority;
00311 
00312     for( uxPriority = 0; uxPriority < configMAX_CO_ROUTINE_PRIORITIES; uxPriority++ )
00313     {
00314         vListInitialise( ( List_t * ) &( pxReadyCoRoutineLists[ uxPriority ] ) );
00315     }
00316 
00317     vListInitialise( ( List_t * ) &xDelayedCoRoutineList1 );
00318     vListInitialise( ( List_t * ) &xDelayedCoRoutineList2 );
00319     vListInitialise( ( List_t * ) &xPendingReadyCoRoutineList );
00320 
00321     /* Start with pxDelayedCoRoutineList using list1 and the
00322     pxOverflowDelayedCoRoutineList using list2. */
00323     pxDelayedCoRoutineList = &xDelayedCoRoutineList1;
00324     pxOverflowDelayedCoRoutineList = &xDelayedCoRoutineList2;
00325 }
00326 /*-----------------------------------------------------------*/
00327 
00328 BaseType_t xCoRoutineRemoveFromEventList( const List_t *pxEventList )
00329 {
00330 CRCB_t *pxUnblockedCRCB;
00331 BaseType_t xReturn;
00332 
00333     /* This function is called from within an interrupt.  It can only access
00334     event lists and the pending ready list.  This function assumes that a
00335     check has already been made to ensure pxEventList is not empty. */
00336     pxUnblockedCRCB = ( CRCB_t * ) listGET_OWNER_OF_HEAD_ENTRY( pxEventList );
00337     ( void ) uxListRemove( &( pxUnblockedCRCB->xEventListItem ) );
00338     vListInsertEnd( ( List_t * ) &( xPendingReadyCoRoutineList ), &( pxUnblockedCRCB->xEventListItem ) );
00339 
00340     if( pxUnblockedCRCB->uxPriority >= pxCurrentCoRoutine->uxPriority )
00341     {
00342         xReturn = pdTRUE;
00343     }
00344     else
00345     {
00346         xReturn = pdFALSE;
00347     }
00348 
00349     return xReturn;
00350 }
